class App extends React.Component {
  arrowHandleClick = (e) => {
    e.preventDefault()
    alert("The first link was clicked")
  }
  handleClick(e) {
    e.preventDefault()
    alert("The second link was clicked")
  }
  render(){
    return (
      <div>
        <button className="btn btn-primary" onClick={() => alert("Its works")}>
          Click on this button
        </button>
        <a href="#" className="btn btn-primary" onClick={this.arrowHandleClick}>
          Click first link
        </a>
        <a href="#" onClick={this.handleClick}>
          Click second link
        </a>
      </div>
    )
  }
}

export default App;
